/* eslint-disable no-lone-blocks */
import React from 'react';
function universeNumber(){
  this.setState.universe++;
  return this.state.universe+"";
}
function galaxyNumber(){
  this.setState.galaxy++;
  return this.state.galaxy+"";
}
function solarsystemNumber(){
    this.setState.solarsystem++;
    return this.state.solarsystem+"";
}
function starNumber(){  
    this.setState.star++;
    return this.state.star+"";
}
function planetNumber(){
    this.setState.planet++;
    return this.state.planet+"";
}
function blackholeNumber(){
    this.setState.blackhole++;
    return this.state.blackhole+"";
}
export default class ApiCall extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      universe: 0,
      galaxy: 0,
      solarsystem: 0,
      star:0 ,
      planet:0,
      blackhole:0
    };


  }

  componentDidMount() {
    fetch('http://localhost:8080/erick')
    .then(res => res.json())
    .then((data) => {
      this.setState({ items: data })
      
    })
    .catch()
  }
  
  
      
      //percorre todo o json recebido
    render(){
      var dados = this.state.items;
      //console.log(dados);
      /*
      for(let u=0;u<this.state.items.length;u++){         
        console.log(this.state.items[u].name);
        for(let i = 0;i<this.state.items[u].galaxyList.length;i++){
            console.log(this.state.items[u].galaxyList[i].name);
            for(let j = 0;j<this.state.items[u].galaxyList[i].solarSystem.length;j++){
                console.log(this.state.items[u].galaxyList[i].solarSystem[j].name);
                for( let p=0;p<this.state.items[u].galaxyList[i].solarSystem[j].planet.length;p++){
                    console.log(this.state.items[u].galaxyList[i].solarSystem[j].planet[p].name);
                }
                for( let s=0;s<this.state.items[u].galaxyList[i].solarSystem[j].star.length;s++){
                    console.log(this.state.items[u].galaxyList[i].solarSystem[j].star[s].name);
                }
                for( let b=0;b<this.state.items[u].galaxyList[i].solarSystem[j].blackHole.length;b++){
                    console.log(this.state.items[u].galaxyList[i].solarSystem[j].blackHole[b].name);
                }
    
            }
    
    
        }
      }
      */


      return(
        //fffffffffffffffff
          <div className="Listarrecebidas">
            <ul id="Lists"> 
          

            </ul>  
                   
            {
            
             
            dados.forEach((universe)=>{ 
               //Listsuniverse Listsgalaxy Listssolarsystem Listsstar Listsplanet Listsblackhole
              document.getElementById("Lists").innerHTML = document.getElementById("Lists").innerHTML+"<ul id='Listsuniverse'></ul>";                 
                document.getElementById("Listsuniverse"+universeNumber()).innerHTML = document.getElementById("Listsuniverse"+universe).innerHTML+"<pre><li>"+universe.name+"</li></pre>";     
                  universe++
                  {universe.galaxyList.forEach((galaxy)=>{
                        
                        document.getElementById("Lists").innerHTML = document.getElementById("Lists").innerHTML+"<pre>  "+galaxy.name+"</pre>"; 
         
                          galaxy.solarSystem.forEach((solarsystem)=>{
                                
                                  document.getElementById("Lists").innerHTML = document.getElementById("Lists").innerHTML+"<pre>    "+solarsystem.name+"</pre>";    
                     
                                  solarsystem.blackHole.forEach((blackhole)=>{
                                    
                                    document.getElementById("Lists").innerHTML = document.getElementById("Lists").innerHTML+"<pre>       "+blackhole.name+"</pre>";    
                                  })
           
                                  solarsystem.planet.forEach((planet)=>{
                                    
                                    document.getElementById("Lists").innerHTML = document.getElementById("Lists").innerHTML+"<pre>       "+planet.name+"</pre>";  
                                  })
                          
                                  solarsystem.star.forEach((star)=>{
                                    
                                    document.getElementById("Lists").innerHTML = document.getElementById("Lists").innerHTML+"<pre>       "+star.name+"</pre>";   
                                  })
                                  
                                  
                          })
                         
                          
                  })}
            
                
            })
            
            }
          
          </div>
      );
    }
       

 
    
  }
